import React from 'react'

const ListItem = ({post}) => {
    const absolutePath = "C:\\Users\\asus\\Desktop\\my_projects\\python\\MyBlog\\backend";
    const relativePath = "/media/photos/23/06/04/download.jpg";

    const imagePath = `${absolutePath}${relativePath}`;

  return (
    <div className='post-card'>
        <img className="post-cover" src={post.cover} alt={post.title} />
        <h1>{post.cover}</h1>
        <div className="post-content">
            <h2 className="post-title">{post.title}</h2>
            <p className="post-summary">{post.summary}</p>
            <div className="post-details">
            <span className="post-category">{post.category}</span>
            <span className="post-date">{new Date(post.date_created).toDateString()}</span>
            </div>
        
        </div>
    </div>
  )
}

export default ListItem
