import React, { useEffect, useState } from 'react';
import axios from 'axios';

const Post = ({ match }) => {
  const { postId } = match.params;
  const [post, setPost] = useState(null);

  useEffect(() => {
    const decodedPostId = decodeURIComponent(postId);
    getPostContent(decodedPostId);
  }, [postId]);

  const getPostContent = async (decodedPostId) => {
    try {
      const response = await axios.get(`http://127.0.0.1:8000/${decodedPostId}`);
      if (response.status === 200) {
        const data = response.data;
        setPost(data);
      } else {
        throw new Error('Failed to fetch post content');
      }
    } catch (error) {
      console.error(error);
    }
  };

  if (!post) {
    return <div>Loading post...</div>;
  }

  return (
    <div>
      <h1>{post.title}</h1>
      <p>{post.content}</p>
      <p>hello </p>
    </div>
  );
};

export default Post;
