import React , {useEffect} from 'react';
import { BrowserRouter as Router, NavLink } from 'react-router-dom';
import './NavBar.css';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faFacebook, faInstagram, faLinkedin } from '@fortawesome/free-brands-svg-icons';
import { faBars } from '@fortawesome/free-solid-svg-icons';

const NavBar = () => {
    

    const ToggleMenu = () => {
        useEffect(() => {
            const element = document.querySelector('.menu');
        
            if (getComputedStyle(element).display === 'none') {
              // The element has the display flex attribute
            } else {
              // The element does not have the display flex attribute
            }
          }, []);
    };
  return (
    <Router>
        <div className='Container'>
            <div className='NavBar'>
                <div className='menu-icon' onClick={ToggleMenu}>
                    <FontAwesomeIcon icon={faBars} style={{ color: 'black', fontSize: '1rem' }}/>
                </div>
                <div className='logo'>
                    <h1>Oussama</h1>
                </div>
                <div className='menu' >
                    <ul>
                        <li>
                            <NavLink exact to="/" activeClassName="active-link" style={{ color: 'black' , textDecoration : 'none' }}>
                                Home
                            </NavLink>
                        </li>
                        <li>
                            <NavLink to="/newsletters" activeClassName="active-link" style={{ color: 'black' , textDecoration : 'none' }}>
                                Newsletters
                            </NavLink>
                        </li>
                        <li>
                            <NavLink to="/about" activeClassName="active-link" style={{ color: 'black' , textDecoration : 'none' }}>
                                About us
                            </NavLink>
                        </li>
                    </ul>
                </div>
                <div className='icons'>
                    <FontAwesomeIcon icon={faFacebook} style={{ color: 'black', fontSize: '1rem', marginLeft: '2rem' }} />
                    <FontAwesomeIcon icon={faInstagram} style={{ color: 'black', fontSize: '1rem', marginLeft: '2rem' }} />
                    <FontAwesomeIcon icon={faLinkedin} style={{ color: 'black', fontSize: '1rem', marginLeft: '2rem' }} />
                </div>
            </div>
            <div className='liste'>
                <div className='menu customized' >
                        <ul>
                            <li>
                                <NavLink exact to="/" activeClassName="active-link" style={{ color: 'black' , textDecoration : 'none' }}>
                                    Home
                                </NavLink>
                            </li>
                            <li>
                                <NavLink to="/newsletters" activeClassName="active-link" style={{ color: 'black' , textDecoration : 'none' }}>
                                    Newsletters
                                </NavLink>
                            </li>
                            <li>
                                <NavLink to="/about" activeClassName="active-link" style={{ color: 'black' , textDecoration : 'none' }}>
                                    About us
                                </NavLink>
                            </li>
                        </ul>
                </div>
            </div>
                
        </div>
    </Router>
  );
};

export default NavBar;
